mapper.select:
SELECT CODE_NAME ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT FROM ( SELECT CODE_NAME ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD A, COM_CODE B WHERE A.BOARD_TYPE = B.CODE_ID
AND code_id IN
#{item}
)
WHERE NUMROW BETWEEN (#{pageNo}-1)*10 + 1 AND (#{pageNo})*10

mapper.update:
UPDATE
BOARD SET
BOARD_TITLE    = #{boardTitle}
,BOARD_COMMENT = #{boardComment}
WHERE 
BOARD_TYPE     = #{boardType}
AND BOARD_NUM  = #{boardNum}

mapper.delete:
DELETE
FROM  BOARD
WHERE BOARD_TYPE = #{boardType}
AND   BOARD_NUM  = #{boardNum}
